uninstall_node_modules() {
    print_step "Uninstalling all node_modules directories..."

    trash-put "$PROJECT_ROOT/packages/js/node_modules"
    trash-put "$PROJECT_ROOT/packages/css/node_modules"
    trash-put "$PROJECT_ROOT/node_modules"
    print_success "$PROJECT_ROOT/packages/css/node_modules removed"
    print_success "$PROJECT_ROOT/packages/js/node_modules removed"
    print_success "$PROJECT_ROOT/node_modules removed"

    print_success "Uninstalled all node_modules directories successfully"
}
